pragma solidity ^0.8.13;

/// @title Army factory interface
/// @notice Contains instance creator function
interface IWorldAssetFactory {
    // Errors

    /// @notice Thrown when attempting to call action which can only be called by world or world asset
    error OnlyWorldOrWorldAsset();

    // Functions

    /// @notice Creates new world asset, sets it into the world and initializes it
    /// @param worldAddress World address
    /// @param eraNumber Era number
    /// @param assetGroupId Asset group id
    /// @param assetTypeId Asset type id
    /// @param initParams Init params
    /// @return worldAssetAddress World asset address
    function create(
        address worldAddress,
        uint256 eraNumber,
        bytes32 assetGroupId,
        bytes32 assetTypeId,
        bytes memory initParams
    ) external returns (address worldAssetAddress);
}
","keccak256":"0xd682f8efaf44d830e23277e9d50b3761db83f19d2f963c383b899df922c1e806","license":"MIT"},"contracts/core/assets/army/IArmy.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.13;

import "../../IWorld.sol";
import "../battle/IBattle.sol";
import "../settlement/ISettlement.sol";
import "../siege/ISiege.sol";

/// @title Army interface
/// @notice Functions to read state/modify state in order to get current army parameters and/or interact with it
interface IArmy {
    struct ManeuverInfo {
        uint64 beginTime;
        uint64 endTime;
        uint64 destinationPosition;
        uint64 secretDestinationRegionId;
        bytes32 secretDestinationPosition;
    }

    struct StunInfo {
        uint64 beginTime;
        uint64 endTime;
    }

    // State variables

    /// @notice Settlement address to which this army belongs
    /// @dev Immutable, initialized on the army creation
    function relatedSettlement() external view returns (ISettlement);

    /// @notice Position where army currently stands on
    /// @dev Updated when army updates position. It does not take into account if army is maneuvering
    /// @dev To proper query current position use #getCurrentPosition
    function currentPosition() external view returns (uint64);

    /// @notice Battle in which army is on
    /// @dev If army is not in battle returns address(0). It does not take into account if battle is ended but army is not left the battle
    function battle() external view returns (IBattle);

    /// @notice Maneuver info
    /// @dev Updated when army begins maneuvering. It does not take into account if army is ended maneuver by time
    function maneuverInfo() external view returns (
        uint64 beginTime,
        uint64 endTime,
        uint64 destinationPosition,
        uint64 secretDestinationRegionId,
        bytes32 secretDestinationPosition
    );

    /// @notice Stun info
    /// @dev Updated when army stun is applied
    function stunInfo() external view returns (uint64 beginTime, uint64 endTime);

    /// @notice Mapping containing additional unit battle multiplier
    /// @dev Updated when #increaseUnitBattleMultiplier or #decreaseUnitBattleMultiplier is called
    function additionalUnitsBattleMultipliers(bytes32 unitTypeId) external view returns (uint256);

    // Events

    /// @notice Emitted when #updatePosition is called (even though event can be emitted only on the next action related to the current army, de-facto army will update position based on 'maneuverInfo.endTime'
    /// @param settlementAddress Address of the settlement where army currently staying on
    /// @param position Position
    event UpdatedPosition(address settlementAddress, uint64 position);

    /// @notice Emitted when #createBattle is called. Army which attacks another army will emit this event
    /// @param battleAddress Created battle address
    /// @param targetArmyAddress Address of the attacked army
    event BattleCreated(address battleAddress, address targetArmyAddress);

    /// @notice Emitted when army joins battle. At the battle creation both armies (attacker and attacked) will emit this event. Attacker army will be side A and at attacked army will be sideB
    /// @param battleAddress Address of the battle army joined in
    /// @param side Side to which army joined (sideA = 1, sideB = 2)
    event JoinedBattle(address battleAddress, uint256 side);

    /// @notice Emitted when #updateState is called (even though event can be emitted only on the next action related to the current army, de-facto army will exit battle when battle is ended)
    /// @param battleAddress Address of the battle army was in
    event ExitedFromBattle(address battleAddress);

    /// @notice Emitted when #beginOpenManeuver or #beginSecretManeuver or #revealSecretManeuver is called
    /// @param position Position army is maneuvering to (0 if secret maneuver)
    /// @param secretDestinationRegionId Secret destination regionId (not zero if secret maneuver)
    /// @param secretDestinationPosition Secret destination position (not zero if secret maneuver)
    /// @param beginTime Time at which maneuver began
    /// @param endTime Time at which maneuver will end (0 if secret maneuver)
    /// @param tokensToSpendOnAcceleration Amount of tokens to spend on acceleration (Food for open maneuver, Wood for secret maneuver)
    event ManeuveringBegan(
        uint64 position,
        uint64 secretDestinationRegionId,
        bytes32 secretDestinationPosition,
        uint256 beginTime,
        uint256 endTime,
        uint256 tokensToSpendOnAcceleration
    );

    /// @notice Emitted when #cancelSecretManeuver is called
    event SecretManeuverCancelled();

    /// @notice Emitted when #_applyStun is called
    /// @param stunBeginTime Stun begin time
    /// @param stunEndTime Stun end time
    event StunApplied(uint64 stunBeginTime, uint64 stunEndTime);

    /// @notice Emitted when #demilitarize is called
    /// @param unitTypeIds Unit type ids demilitarized
    /// @param unitsAmounts Amount of units demilitarized
    event UnitsDemilitarized(bytes32[] unitTypeIds, uint256[] unitsAmounts);

    // Errors

    /// @notice Thrown when attempting to call action which only possible to be called by ruler, world or world asset
    error OnlyRulerOrWorldAssetFromSameEra();

    /// @notice Thrown when attempting to call action which is blocked when army is stunned
    error ArmyIsStunned();

    /// @notice Thrown when attempting to call action which is blocked when army is maneuvering
    error ArmyIsManeuvering();

    /// @notice Thrown when attempting to call action which is blocked when army is in battle
    error ArmyIsInBattle();

    /// @notice Thrown when attempting to call action which is blocked when army is in siege
    error ArmyIsInSiege();

    /// @notice Thrown when attempting to begin maneuver to same position
    error ArmyCannotManeuverToSamePosition();

    /// @notice Thrown when attempting to maneuver to position without settlement
    error ArmyCannotManeuverToPositionWithoutSettlement();

    /// @notice Thrown when attempting to maneuver without units to non-home position
    error ArmyWithoutUnitsCannotManeuverToNotHomeSettlement();

    /// @notice Thrown when attempting to maneuver without units but with acceleration
    error ArmyWithoutUnitsCannotAccelerate();

    /// @notice Thrown when attempting to secretly maneuver without units
    error ArmyWithoutUnitsCannotBeginSecretManeuver();

    /// @notice Thrown when attempting to reveal or cancel secret maneuver
    error ArmyIsNotManeuveringSecretly();

    /// @notice Thrown when attempting to reveal secret maneuver with invalid reveal info
    error WrongSecretManeuverRevealInfo();

    /// @notice Thrown when attempting to reveal secret maneuver with valid reveal info but wrong region
    error SecretManeuverRevealNotPossibleToNotSpecifiedRegion();

    /// @notice Thrown when attempting to reveal secret maneuver at invalid time
    error SecretManeuverRevealNotPossibleAtThisTime();

    /// @notice Thrown when attempting to demilitarize with invalid input
    error WrongDemilitarizationInput();

    /// @notice Thrown when attempting to demilitarize more units than army currently have
    error NotEnoughUnitsForDemilitarization();

    /// @notice Thrown when attempting to start battle by attacking army not on same position as current army
    error ArmyCannotAttackAnotherArmyIfTheyAreNotOnSamePosition();

    /// @notice Thrown when attempting to start battle by providing army address to attack which is not part of current era
    error ArmyCannotAttackNotCurrentEraArmy();

    /// @notice Thrown when attempting to start battle by providing army address which is same as current army
    error ArmyCannotAttackItself();

    /// @notice Thrown when attempting to join battle by providing invalid battle address
    error ArmyCannotJoinToNotCurrentEraBattle();

    /// @notice Thrown when attempting to join battle with army which has zero units in it
    error ArmyWithoutUnitsCannotJoinBattle();

    /// @notice Thrown when attempting to join battle by providing invalid side
    error WrongJoinSide();

    /// @notice Thrown when attempting to join battle while its not in lobby phase
    error ArmyCannotJoinToBattleNotInLobbyPhase();

    /// @notice Thrown when attempting to join battle which is not at same position as current army
    error ArmyCannotJoinToBattleNotAtSamePosition();

    /// @notice Thrown when attempting to modify siege of own settlement
    error ArmyCannotBesiegeOwnSettlement();

    /// @notice Thrown when attempting to modify siege by providing invalid robbery multiplier
    error WrongRobberyMultiplierSpecified();

    /// @notice Thrown when attempting to modify siege in result of which army will become liquidatable
    error ArmyCannotModifySiegeUnitsToLiquidatableState();

    /// @notice Thrown when attempting to use more resources for acceleration than related building treasury has
    error ArmyCannotUseMoreResourcesForAccelerationThanBuildingTreasuryHas();

    /// @notice Thrown when attempting to use accelerate maneuver from cultists settlement with non zero cultists
    error ArmyCannotAccelerateManeuverFromCultistsSettlementWithNonZeroCultistsArmy();

    // Functions

    /// @notice Updates army state to the current block
    /// @dev Called on every action which are based on army state and time
    function updateState() external;

    /// @notice Begins open maneuver to specified position
    /// @dev Even though position can be artificial, army can move only to settlement
    /// @param position Position of settlement to move to
    /// @param foodToSpendOnAcceleration Amount of food army will take from current position settlements FARM in order to decrease total time army will take to get to destination position
    function beginOpenManeuver(uint64 position, uint256 foodToSpendOnAcceleration) external;

    /// @notice Begins secret maneuver to secret position
    /// @dev Caller must be aware of the rules applied to revealing destination position otherwise army may be punished
    /// @param secretDestinationRegionId Secret destination region id
    /// @param secretDestinationPosition Secret destination position
    function beginSecretManeuver(uint64 secretDestinationRegionId, bytes32 secretDestinationPosition) external;

    /// @notice Reveals secret maneuver
    /// @dev In order to successfully reveal 'secretDestinationPosition' - 'destination position' and 'revealKey' must be valid
    /// @dev Validity of verified by 'keccak256(abi.encodePacked(destinationPosition, revealKey)) == secretDestinationPosition'
    /// @param destinationPosition Destination position
    /// @param revealKey Reveal key
    /// @param woodToSpendOnAcceleration Wood to spend on acceleration
    function revealSecretManeuver(uint64 destinationPosition, bytes32 revealKey, uint256 woodToSpendOnAcceleration) external;

    /// @notice Cancels secret maneuver
    /// @dev Can be cancelled by army owner
    function cancelSecretManeuver() external;

    /// @notice Demilitarizes part of the army. Demilitarization provides prosperity to the settlement army is currently staying on
    /// @dev Even though demilitarization of 0 units may seem reasonable, it is disabled
    /// @param unitTypeIds Unit type ids for demilitarization
    /// @param unitsAmounts Amount of units to demilitarize
    function demilitarize(bytes32[] memory unitTypeIds, uint256[] memory unitsAmounts) external;

    /// @notice Begins battle with another army if both are not in battle
    /// @dev Creates IBattle and sets both armies in created battle
    /// @param armyAddress Address of the army this army will attack
    /// @param maxUnitTypeIdsToAttack Max unit type ids to attack
    /// @param maxUnitsToAttack Max units to attack
    function beginBattle(
        address armyAddress,
        bytes32[] calldata maxUnitTypeIdsToAttack,
        uint256[] calldata maxUnitsToAttack
    ) external;

    /// @notice Joins current army in battle to the provided side
    /// @dev Maneuvering army is able to join battle only if caller is another army (drags it into battle)
    /// @param battleAddress Battle address army will join
    /// @param side Side of the battle army will join (sideA = 1, sideB = 2)
    function joinBattle(address battleAddress, uint256 side) external;

    /// @notice Burns units from the army
    /// @dev Can only be called by world or world asset
    /// @param unitTypeIds Unit type ids for burning
    /// @param unitsAmounts Amount of units for burning for every unit type
    function burnUnits(bytes32[] memory unitTypeIds, uint256[] memory unitsAmounts) external;

    /// @notice Liquidates units from the army
    /// @dev Can only be called by world or world asset
    /// @param unitTypeIds Unit type ids for liquidation
    /// @param unitsAmounts Amount of units for liquidation
    function liquidateUnits(bytes32[] memory unitTypeIds, uint256[] memory unitsAmounts) external;

    /// @notice Calculates current position taking to the account #maneuverInfo
    /// @dev This method should be used to determine real army position
    /// @return position Position
    function getCurrentPosition() external view returns (uint64 position);

    /// @notice Modifies army siege params
    /// @dev Provides ability to atomically setup/re-setup siege
    /// @param unitTypeIds Unit type ids
    /// @param toAddIndication Indication array whether to add units or to withdraw (add = true, withdraw = false)
    /// @param unitsAmounts Amounts of units to add/withdraw
    /// @param newRobberyMultiplier New robbery multiplier
    function modifySiege(
        bytes32[] calldata unitTypeIds,
        bool[] calldata toAddIndication,
        uint256[] calldata unitsAmounts,
        uint256 newRobberyMultiplier
    ) external;

    /// @notice Swaps accumulated robbery points in siege for resource from building treasury
    /// @dev Amount of points will be taken may be lesser if building does not have resources in its treasury
    /// @param buildingAddress Address of the building treasury of which will be robbed
    /// @param pointsToSpend Amount of points to spend for resources
    function swapRobberyPointsForResourceFromBuildingTreasury(address buildingAddress, uint256 pointsToSpend) external;

    /// @notice Calculates total siege support of the army
    /// @dev For every unit type placed in siege calculates sum of all of them
    /// @return totalSiegeSupport Total siege support of the army
    function getTotalSiegeSupport() external view returns (uint256 totalSiegeSupport);

    /// @notice Return owner of the army
    /// @dev Same as owner of the settlement to which this army belongs
    /// @return ownerAddress Address of the owner of the army
    function getOwner() external view returns (address ownerAddress);

    /// @notice Calculates is army at home position
    /// @dev Takes into account if army maneuver is ended (by time)
    /// @return isAtHomePosition Is army at home position
    function isAtHomePosition() external view returns (bool isAtHomePosition);

    /// @notice Calculates is army maneuvering (openly or secretly)
    /// @dev Takes into account if army maneuver is ended (by time)
    /// @return isManeuvering Is maneuvering
    function isManeuvering() external view returns (bool isManeuvering);

    /// @notice Increases unit battle multiplier
    /// @dev Can only be called by world or world asset
    /// @param unitTypeId Unit type id
    /// @param unitBattleMultiplier Unit battle multiplier
    function increaseUnitBattleMultiplier(bytes32 unitTypeId, uint256 unitBattleMultiplier) external;

    /// @notice Decreases unit battle multiplier
    /// @dev Can only be called by world or world asset
    /// @param unitTypeId Unit type id
    /// @param unitBattleMultiplier Unit battle multiplier
    function decreaseUnitBattleMultiplier(bytes32 unitTypeId, uint256 unitBattleMultiplier) external;

    /// @notice Applies army stun by settlement ruler
    /// @dev Provides ability to self stun owned army
    /// @param stunDuration Stun duration
    function applySelfStun(uint64 stunDuration) external;
}
","keccak256":"0xd391d4e9d7c5cec4c98427df38b30abfe717e223e578d16d3ab86d2f881b5633","license":"MIT"},"contracts/core/assets/battle/IBattle.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.13;

import "../../IWorld.sol";

/// @title Battle interface
/// @notice Functions to read state/modify state in order to get current battle parameters and/or interact with it
interface IBattle {
    struct BattleTimeInfo {
        uint64 beginTime;
        uint64 duration;
        uint64 endTime;
    }

    // State variables

    /// @notice Position at which battle is being held
    /// @dev Immutable, initialized on the battle creation
    function position() external view returns (uint64);

    /// @notice Mapping that contains units amount by side and unit type
    /// @dev Updated when army joins side
    /// @param side Side of which query units amount (sideA = 1, sideB = 2)
    /// @param unitTypeId Unit type id
    /// @return unitsAmount Amount of units by specified side and unit type
    function sideUnitsAmount(uint256 side, bytes32 unitTypeId) external view returns (uint256 unitsAmount);

    /// @notice Mapping that contains amount of units by army address and unit type
    /// @dev Updated when army joins battle
    /// @param armyAddress Army address
    /// @param unitTypeId Unit type id
    /// @return unitsAmount Amount of units by army address and unit type
    function armyUnitsAmount(address armyAddress, bytes32 unitTypeId) external view returns (uint256 unitsAmount);

    /// @notice Mapping that contains unit multiplier by army address and unit type
    /// @dev Updated when army joins battle
    /// @param armyAddress Army address
    /// @param unitTypeId Unit type id
    /// @return unitAmountMultiplier Unit amount multiplier
    function armyUnitsAdditionalMultipliers(address armyAddress, bytes32 unitTypeId) external view returns (uint256 unitAmountMultiplier);

    /// @notice Mapping that contains amount of casualties
    /// @dev Updated when #acceptArmyInBattle is called
    /// @param side Side of which query casualties amount (sideA = 1, sideB = 2)
    /// @param unitTypeId Unit type id
    /// @return casualtiesCount Amount of casualties by side and unit type
    function casualties(uint256 side, bytes32 unitTypeId) external view returns (uint256 casualtiesCount);

    /// @notice Mapping that contains side at which joined army is on
    /// @dev Updated when #acceptArmyInBattle is called
    /// @param armyAddress Army address
    /// @return armySide Side of specified army (sideA = 1, sideB = 2)
    function armySide(address armyAddress) external view returns (uint256 armySide);

    /// @notice Battle time info
    /// @dev Updated when battle initialized, first armies joined and ended (#initBattle, #acceptArmyInBattle, #endBattle)
    /// @return beginTime Time when battle is began
    /// @return duration Battle duration, initialized when first two armies joined
    /// @return endTime Time when battle is ended
    function battleTimeInfo()
        external
        view
        returns (
            uint64 beginTime,
            uint64 duration,
            uint64 endTime
        );

    /// @notice Winning side
    /// @dev Updated when #endBattle is called
    /// @return winningSide Winning side (no winner = 0, sideA = 1, sideB = 2)
    function winningSide() external view returns (uint256 winningSide);

    // Events

    /// @notice Emitted when army joined battle
    /// @param armyAddress Address of the joined army
    /// @param side Side to which army is joined (sideA = 1, sideB = 2)
    event ArmyJoined(address armyAddress, uint256 side);

    /// @notice Emitted when #endBattle is called
    /// @param endTime Time at which battle is ended
    event BattleEnded(uint256 endTime);

    // Errors

    /// @notice Thrown when attempting to begin battle by attacking army with MAX amount of units to attack, but their value increased to more than MAX
    error BattleCannotBeCreatedWhenArmyUnitsExceedDesiredAmountToAttack();

    /// @notice Thrown when attempting to begin battle by attacking cultists army with desire to draw zero units to the battle
    error BattleCannotBeCreatedByDesiringToAttackCultistsArmyWithoutUnits();

    /// @notice Thrown when attempting to begin battle with armies either of which has zero units
    error BattleCannotBeCreatedWithArmiesHavingZeroUnits();

    /// @notice Thrown when attempting to start battle by attacking army when maneuver duration left less than minimum battle duration
    error BattleCannotBeCreatedWhenAttackedArmyIsAlmostOnAnotherPosition();

    /// @notice Thrown when attempting to accept cultists army to the battle but their amount got smaller than desired to attack
    error BattleCannotAcceptCultistsArmyWhenCultistsAmountChangedToLowerValueThanDesired();

    /// @notice Thrown when attempting to finish battle while time for it has not yet come
    error BattleCannotBeFinishedAtThisTime();

    /// @notice Thrown when attempting to finish battle when it is already finished
    error BattleCannotBeFinishedMoreThanOnce();

    // Functions

    /// @notice Accepts army in battle
    /// @dev Even though function is opened, it can only be called by world asset
    /// @param armyAddress Army address
    /// @param side Side to which army will join
    function acceptArmyInBattle(address armyAddress, uint256 side) external;

    /// @notice Ends battle
    /// @dev Sets end time
    function endBattle() external;

    /// @notice Calculates casualties for first battle stage
    /// @dev Uses values from battles' sideUnitsAmount in order to calculate casualties (can be executed while battle is still not fully formed)
    /// @return sideACasualties Side A casualties
    /// @return sideBCasualties Side B casualties
    /// @return stageParams Stage params (encoded abi.encode(sideAOffense, sideBOffense, sideADefence, sideBDefence))
    function calculateStage1Casualties()
        external
        view
        returns (
            uint256[] memory sideACasualties,
            uint256[] memory sideBCasualties,
            bytes memory stageParams
        );

    /// @notice Calculates casualties for second battle stage (based on casualties from first battle stage)
    /// @dev Uses values from battles' sideUnitsAmount in order to calculate casualties (can be executed while battle is still not fully formed)
    /// @param stage1SideACasualties Stage 1 side A casualties
    /// @param stage1SideBCasualties Stage 1 side B casualties
    /// @return sideACasualties Side A casualties
    /// @return sideBCasualties Side B casualties
    /// @return stageParams Stage params (encoded abi.encode(sideAOffense, sideBOffense, sideADefence, sideBDefence))
    function calculateStage2Casualties(
        uint256[] memory stage1SideACasualties,
        uint256[] memory stage1SideBCasualties
    )
        external
        view
        returns (
            uint256[] memory sideACasualties,
            uint256[] memory sideBCasualties,
            bytes memory stageParams
        );

    /// @notice Calculates casualties for all battle stages
    /// @dev Uses values from battles' sideUnitsAmount in order to calculate casualties (can be executed while battle is still not fully formed)
    /// @return sideACasualties Side A casualties
    /// @return sideBCasualties Side A casualties
    /// @return winningSide Winning side (0 - both sides lost, 1 - side A Won, 2 - side B Won
    function calculateAllCasualties()
        external
        view
        returns (
            uint256[] memory sideACasualties,
            uint256[] memory sideBCasualties,
            uint256 winningSide
        );

    /// @notice Calculates battle duration
    /// @dev Returns same value as #calculateBattlesDuration but without the need to provide all parameters
    /// @param isCultistsAttacked Is cultists attacked
    /// @param maxBattleDuration Max allowed battle duration
    /// @param sideAUnitsAmount Side A units amount
    /// @param sideBUnitsAmount Side B units amount
    /// @return battleDuration Battle duration
    function getBattleDuration(
        bool isCultistsAttacked,
        uint256 maxBattleDuration,
        uint256 sideAUnitsAmount,
        uint256 sideBUnitsAmount
    ) external view returns (uint64 battleDuration);

    /// @notice Calculates if battle can be ended
    /// @dev Checks if endTime is set and current block.timestamp > beginTime + duration
    /// @return canEndBattle Can battle be ended
    function canEndBattle() external view returns (bool canEndBattle);

    /// @notice Calculates if battle is ended
    /// @dev Checks if endTime is not zero
    /// @return isEndedBattle Is ended battle
    function isEndedBattle() external view returns (bool isEndedBattle);

    /// @notice Calculates casualties for specified army
    /// @dev Provides valid results only for ended battle
    /// @param armyAddress Address of army presented in battle
    /// @return isArmyWon Is army won
    /// @return unitsAmounts Amount of casualties for related unit types
    function calculateArmyCasualties(address armyAddress)
        external
        view
        returns (
            bool isArmyWon,
            uint256[] memory unitsAmounts
        );

    /// @notice Calculates if lobby is opened
    /// @dev Calculates if lobby is opened
    /// @return isLobbyTime Is lobby is opened
    function isLobbyTime() external view returns (bool isLobbyTime);

    /// @notice Calculates battle duration based on specified parameters
    /// @dev globalMultiplier, baseBattleDuration parameters from registry
    /// @param globalMultiplier Global multiplier (from registry)
    /// @param baseBattleDuration Base battle duration (from registry)
    /// @param minBattleDuration Minimum battle duration (from registry)
    /// @param isCultistsAttacked Is cultists attacked
    /// @param units1 Amount of units from attacker army
    /// @param units2 Amount of units from attacked army
    /// @param maxBattleDuration Max allowed battle duration
    /// @return battleDuration Battle duration
    function calculateBattleDuration(
        uint256 globalMultiplier,
        uint256 baseBattleDuration,
        uint256 minBattleDuration,
        bool isCultistsAttacked,
        uint256 units1,
        uint256 units2,
        uint256 maxBattleDuration
    ) external view returns (uint64 battleDuration);
}
","keccak256":"0x86e876b929e90f81ec992f20fbd062bd4fcc41777f2a5cb9e32a8ea33e886ca3","license":"MIT"},"contracts/core/assets/building/IBuilding.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.13;

import "../../IWorld.sol";
import "../settlement/ISettlement.sol";

/// @title Building interface
/// @notice Functions to read state/modify state in order to get current building parameters and/or interact with it
interface IBuilding {
    struct BasicProduction {
        uint256 level;
        uint256 coefficient;
    }

    struct AdvancedProduction {
        uint256 level;
        uint256 coefficient;
        uint256 additionalWorkersCapacityMultiplier;
        uint256 toBeProducedTicks;
    }

    struct ProductionInfo {
        uint256 lastUpdateStateTime;
        uint256 lastUpdateStateRegionTime;
        uint256 readyToBeDistributed;
        uint256 totalDebt;
    }

    struct ProductionResultItem {
        bytes32 resourceTypeId;
        uint256 balanceDelta;
        bool isProduced;
    }

    struct ProductionConfigItem {
        bytes32 resourceTypeId;
        uint256 amountPerTick;
        bool isProducing;
    }

    // State variables

    /// @notice Building type id
    /// @dev Immutable, initialized on the building creation
    function buildingTypeId() external view returns (bytes32 buildingTypeId);

    /// @notice Settlement address to which this building belongs
    /// @dev Immutable, initialized on the building creation
    function relatedSettlement() external view returns (ISettlement);

    /// @notice Basic production
    /// @dev Contains basic production upgrade data
    /// @return level Basic production level
    /// @return coefficient Basic production coefficient
    function basicProduction() external view returns (
        uint256 level,
        uint256 coefficient
    );

    /// @notice Advanced production
    /// @dev Contains advanced production upgrade data
    /// @return level Advanced production level
    /// @return coefficient Advanced production coefficient
    /// @return additionalWorkersCapacityMultiplier Additional workers capacity multiplier
    /// @return toBeProducedTicks To be produced ticks of producing resource
    function advancedProduction() external view returns (
        uint256 level,
        uint256 coefficient,
        uint256 additionalWorkersCapacityMultiplier,
        uint256 toBeProducedTicks
    );

    /// @notice Upgrade cooldown end time
    /// @dev Updated when #upgradeBasicProduction or #upgradeAdvancedProduction is called
    /// @return upgradeCooldownEndTime Upgrade cooldown end time
    function upgradeCooldownEndTime() external view returns (uint256 upgradeCooldownEndTime);

    /// @notice Amount of prosperity given
    /// @dev Contains last written given prosperity amount by building treasury
    /// @return givenProsperityAmount Given prosperity amount
    function givenProsperityAmount() external view returns (uint256 givenProsperityAmount);

    /// @notice Contains production info of the building
    /// @dev Contains information related to how production is calculated
    /// @return lastUpdateStateTime Time at which last #updateState is called
    /// @return lastUpdateStateRegionTime Region time at which last #updateState is called
    /// @return readyToBeDistributed Amount of produced resource ready to be distributed
    /// @return totalDebt Total debt
    function productionInfo()
        external
        view
        returns (
            uint256 lastUpdateStateTime,
            uint256 lastUpdateStateRegionTime,
            uint256 readyToBeDistributed,
            uint256 totalDebt
        );

    /// @notice Distribution id
    /// @dev Initialized on creation and updated on #resetDistribution
    function distributionId() external view returns (uint256);

    /// @notice Produced resource debt
    /// @dev Updated when #distributeToSingleHolder or #distributeToAllShareholders is called
    function producedResourceDebt(address holder) external view returns (uint256);

    // Events

    /// @notice Emitted when #doBasicProductionUpgrade is called
    /// @param newBasicProductionLevel New basic production level
    event BasicProductionUpgraded(uint256 newBasicProductionLevel, uint256 newBasicProductionCoefficient);

    /// @notice Emitted when #doAdvancedProductionUpgrade is called
    /// @param newAdvancedProductionLevel New advanced production level
    event AdvancedProductionUpgraded(uint256 newAdvancedProductionLevel, uint256 newAdvancedProductionCoefficient);

    /// @notice Emitted when #increaseAdditionalWorkersCapacityMultiplier or #decreaseAdditionalWorkersCapacityMultiplier
    /// @param newAdditionalWorkersCapacityMultiplier New additional workers capacity multiplier
    event AdvancedProductionAdditionalWorkersCapacityMultiplierUpdated(uint256 newAdditionalWorkersCapacityMultiplier);

    /// @notice Emitted when #distribute is called (when resources from production are distributed to building token holders)
    /// @param resourceTypeId Resource type id
    /// @param holder Receiver address
    /// @param amount Amount of distributed resources
    event DistributedToShareHolder(bytes32 resourceTypeId, address holder, uint256 amount);

    /// @notice Emitted when #_setDefaultDistribution is called
    /// @param newDistributionId New distribution id
    event DistributionCreated(uint256 newDistributionId);

    /// @notice Emitted when productionInfo is updated
    /// @param lastUpdateStateTime Last update state time
    /// @param lastUpdateStateRegionTime Last update state region time
    /// @param readyToBeDistributed Ready to be distributed
    /// @param totalDebt Total debt
    event ProductionInfoUpdated(
        uint256 lastUpdateStateTime,
        uint256 lastUpdateStateRegionTime,
        uint256 readyToBeDistributed,
        uint256 totalDebt
    );

    /// @notice Emitted when producedResourceDebt updated for distributionNftHolder
    /// @param distributionNftHolder Distribution nft holder
    /// @param newDebt New debt
    event ProducedResourceDebtUpdated(
        address distributionNftHolder,
        uint256 newDebt
    );

    // Errors

    /// @notice Thrown when attempting to call action which can be called only by Distributions
    error OnlyDistributions();

    /// @notice Thrown when attempting to call action which can be called only by settlement owner
    error OnlySettlementOwner();

    /// @notice Thrown when attempting to call action which can be called only by ruler or world asset
    error OnlyRulerOrWorldAssetFromSameEra();

    /// @notice Thrown when attempting to reset distribution of building tokens when its not allowed
    error DistributionResetNotAllowedWhenTreasuryThresholdNotMet();

    /// @notice Thrown when attempting to upgrade building when upgrades are on cooldown
    error BuildingCannotBeUpgradedWhileUpgradeIsOnCooldown();

    /// @notice Thrown when attempting to transfer producing resource from building
    error CannotTransferProducingResourceFromBuilding();

    // Functions

    /// @notice Resets current building distribution
    /// @dev Creates new distribution Nft and mints it to current settlement owner
    function resetDistribution() external;

    /// @notice Callback which recalculates production. Called when resources related to production of this building is transferred from/to this building
    /// @dev Even though function is opened, it is auto-called by transfer method. Standalone calls provide 0 impact.
    function handleProductionResourcesChanged() external;

    /// @notice Updates state of this building up to block.timestamp
    /// @dev Updates building production minting treasury and increasing #production.readyToBeDistributed
    function updateState() external;

    /// @notice Updates debts for shareholders whenever their share part changes
    /// @dev Even though function is opened, it can be called only by distributions
    /// @param from From address
    /// @param to To address
    /// @param amount Amount
    function updateDebtsAccordingToNewDistributionsAmounts(
        address from,
        address to,
        uint256 amount
    ) external;

    /// @notice Distributes produced resource to single shareholder
    /// @dev Useful to taking part of the resource from the building for single shareholder (to not pay gas for minting for all shareholders)
    /// @param holder Holder
    function distributeToSingleShareholder(address holder) external;

    /// @notice Distributes produces resource to all shareholders
    /// @dev Useful to get full produced resources to all shareholders
    function distributeToAllShareholders() external;

    /// @notice Calculates building coefficient by provided level
    /// @dev Used to determine max treasury amount and new production coefficients
    /// @param level Building level
    /// @return buildingCoefficient Building coefficient
    function getBuildingCoefficient(uint256 level) external pure returns (uint256 buildingCoefficient);

    /// @notice Calculates amount of workers currently sitting in this building
    /// @dev Same as workers.balanceOf(buildingAddress)
    /// @return assignedWorkersAmount Amount of assigned workers
    function getAssignedWorkers() external view returns (uint256 assignedWorkersAmount);

    /// @notice Calculates real amount of provided resource in building related to its production at provided time
    /// @dev Useful for determination how much of production resource (either producing and spending) at the specific time
    /// @param resourceTypeId Type id of resource related to production
    /// @param timestamp Time at which calculate amount of resources in building. If timestamp=0 -> calculates as block.timestamp
    /// @return resourcesAmount Real amount of provided resource in building related to its production at provided time
    function getResourcesAmount(bytes32 resourceTypeId, uint256 timestamp) external view returns (uint256 resourcesAmount);

    /// @notice Calculates production resources changes at provided time
    /// @dev Useful for determination how much of all production will be burned/produced at the specific time
    /// @param timestamp Time at which calculate amount of resources in building. If timestamp=0 -> calculates as block.timestamp
    /// @return productionResult Production resources changes at provided time
    function getProductionResult(uint256 timestamp) external view returns (ProductionResultItem[] memory productionResult);

    /// @notice Calculates upgrade price by provided level
    /// @dev Useful for determination how much upgrade will cost at any level
    /// @param level Level at which calculate price
    /// @return price Amount of resources needed for upgrade
    function getUpgradePrice(uint256 level) external view returns (uint256 price);

    /// @notice Calculates basic upgrade duration for provided level
    /// @dev If level=1 then returned value will be duration which is taken for upgrading from 1 to 2 level
    /// @param level At which level calculate upgrade duration
    /// @return upgradeCooldownDuration Upgrade cooldown duration
    function getBasicUpgradeCooldownDuration(uint256 level) external view returns (uint256 upgradeCooldownDuration);

    /// @notice Calculates advanced upgrade duration for provided level
    /// @dev If level=1 then returned value will be duration which is taken for upgrading from 1 to 2 level
    /// @param level At which level calculate upgrade duration
    /// @return upgradeCooldownDuration Upgrade cooldown duration
    function getAdvancedUpgradeCooldownDuration(uint256 level) external view returns (uint256 upgradeCooldownDuration);

    /// @notice Upgrades basic production
    /// @dev Necessary resources for upgrade will be taken either from msg.sender or resourcesOwner (if resource.allowance allows it)
    /// @dev If resourcesOwner == address(0) -> resources will be taken from msg.sender
    /// @dev If resourcesOwner != address(0) and resourcesOwner has given allowance to msg.sender >= upgradePrice -> resources will be taken from resourcesOwner
    /// @param resourcesOwner Resources owner
    function upgradeBasicProduction(address resourcesOwner) external;

    /// @notice Upgrades advanced production
    /// @dev Necessary resources for upgrade will be taken either from msg.sender or resourcesOwner (if resource.allowance allows it)
    /// @dev If resourcesOwner == address(0) -> resources will be taken from msg.sender
    /// @dev If resourcesOwner != address(0) and resourcesOwner has given allowance to msg.sender >= upgradePrice -> resources will be taken from resourcesOwner
    /// @param resourcesOwner Resources owner
    function upgradeAdvancedProduction(address resourcesOwner) external;

    /// @notice Calculates current level
    /// @dev Takes into an account if upgrades are ended or not
    /// @return level Current building level
    function getBuildingLevel() external view returns (uint256 level);

    /// @notice Returns production config for current building
    /// @dev Main config that determines which resources is produced/spend by production of this building
    /// @dev ProductionConfigItem.amountPerTick is value how much of resource is spend/produced by 1 worker in 1 tick of production
    /// @return productionConfigItems Production config for current building
    function getConfig() external view returns (ProductionConfigItem[] memory productionConfigItems);

    /// @notice Transfers game resources and workers from building to provided addresses
    /// @dev Removes resources+workers from building in single transaction
    /// @param workersReceiverAddress Workers receiver address (building or settlement)
    /// @param workersAmount Workers amount (in 1e18 precision)
    /// @param resourcesReceiverAddress Resources receiver address
    /// @param resourceTypeIds Resource type ids
    /// @param resourcesAmounts Resources amounts
    function removeResourcesAndWorkers(
        address workersReceiverAddress,
        uint256 workersAmount,
        address resourcesReceiverAddress,
        bytes32[] calldata resourceTypeIds,
        uint256[] calldata resourcesAmounts
    ) external;

    /// @notice Calculates maximum amount of treasury by provided level
    /// @dev Can be used to determine maximum amount of treasury by any level
    /// @param level Building level
    /// @param maxTreasury Maximum amount of treasury
    function getMaxTreasuryByLevel(uint256 level) external view returns (uint256 maxTreasury);

    /// @notice Steals resources from treasury
    /// @dev Called by siege, resources will be stolen into stealer settlement building treasury
    /// @param stealerSettlementAddress Settlement address which will get resources
    /// @param amount Amount of resources to steal and burn
    /// @return stolenAmount Amount of stolen resources
    /// @return burnedAmount Amount of burned resources
    function stealTreasury(
        address stealerSettlementAddress,
        uint256 amount
    ) external returns (uint256 stolenAmount, uint256 burnedAmount);

    /// @notice Burns building treasury
    /// @dev Can be called by world asset
    /// @param amount Amount of resources to burn from treasury
    function burnTreasury(
        uint256 amount
    ) external;

    /// @notice Increases additional workers capacity multiplier
    /// @dev Even though function is opened, it can be called only by world asset
    /// @param capacityAmount Capacity amount
    function increaseAdditionalWorkersCapacityMultiplier(uint256 capacityAmount) external;

    /// @notice Decreases additional workers capacity multiplier
    /// @dev Even though function is opened, it can be called only by world asset
    /// @param capacityAmount Capacity amount
    function decreaseAdditionalWorkersCapacityMultiplier(uint256 capacityAmount) external;

    /// @notice Calculates workers capacity (maximum amount of workers)
    /// @dev Used in determination of determinate maximum amount of workers
    /// @return workersCapacity Workers capacity
    function getWorkersCapacity() external view returns (uint256 workersCapacity);

    /// @notice Calculates producing resource type id for this building
    /// @dev Return value is value from #getConfig where 'isProducing'=true
    /// @return resourceTypeId Type id of producing resource
    function getProducingResourceTypeId() external view returns (bytes32 resourceTypeId);

    /// @notice Calculates treasury amount at specified time
    /// @dev Useful for determination how much treasury will be at specific time
    /// @param timestamp Time at which calculate amount of treasury in building. If timestamp=0 -> calculates as block.timestamp
    /// @return treasuryAmount Treasury amount at specified time
    function getTreasuryAmount(uint256 timestamp) external view returns (uint256 treasuryAmount);

    /// @notice Calculates if building is capable to accept resource
    /// @dev Return value based on #getConfig
    /// @param resourceTypeId Resource type id
    /// @return isResourceAcceptable Is building can accept resource
    function isResourceAcceptable(bytes32 resourceTypeId) external view returns (bool isResourceAcceptable);

    /// @notice Calculates capacity of available workers for advanced production
    /// @dev Difference between #getWorkersCapacity and #getAdditionalWorkersFromAdditionalWorkersCapacityMultiplier
    function getAvailableForAdvancedProductionWorkersCapacity() external view returns (uint256);

    /// @notice Calculates additional workers 'granted' from capacity multiplier
    /// @dev Return value based on current advancedProduction.additionalWorkersCapacityMultiplier
    function getAdditionalWorkersFromAdditionalWorkersCapacityMultiplier() external view returns (uint256);
}
