pragma solidity ^0.8.13;

import "../workersPool/IWorkersPool.sol";
import "../era/IEra.sol";
import "../unitsPool/IUnitsPool.sol";
import "../settlement/ISettlement.sol";
import "../settlementsMarket/ISettlementMarket.sol";

/// @title Region interface
/// @notice Functions to read state/modify state in order to get current region parameters and/or interact with it
interface IRegion {

    // State variables

    /// @notice Workers pool
    /// @dev Immutable, initialized on the region creation
    function workersPool() external view returns (IWorkersPool);

    /// @notice Mapping containing units pool for provided unit type id
    /// @dev Immutable, initialized on the region creation
    function unitsPools(bytes32 unitTypeId) external view returns (IUnitsPool);

    /// @notice Mapping containing units market for provided unit type
    /// @dev Immutable, initialized on the region creation
    function settlementsMarket() external view returns (ISettlementsMarket);

    /// @notice Cultists settlement of this region
    /// @dev Immutable, initialized on the region creation
    function cultistsSettlement() external view returns (ISettlement);

    /// @notice Last cultists summon interval number of this region
    /// @dev Updated when #_summonCultists is called
    function lastCultistsSummonIntervalNumber() external view returns (uint256);

    /// @notice Amount of corruptionIndex in this region
    /// @dev Updated when #increaseCorruptionIndex or #decreaseCorruptionIndex is called
    function corruptionIndex() external view returns (int256);

    /// @notice Region id
    /// @dev Immutable, initialized on the region creation
    function regionId() external view returns (uint64);

    /// @notice Last update time
    /// @dev Updated when #updateRegionTime is called
    function lastUpdateTime() external view returns (uint256);

    /// @notice Last apply state region time
    /// @dev Updated when #updateRegionTime is called
    function lastUpdateRegionTime() external view returns (uint256);

    // Events

    /// @notice Emitted when region initialized
    /// @param workersPoolAddress Workers pool address
    event WorkersPoolCreated(address workersPoolAddress);

    /// @notice Emitted when region initialized
    /// @param settlementsMarketAddress Settlements market address
    event SettlementsMarketCreated(address settlementsMarketAddress);

    /// @notice Emitted when region initialized
    /// @param unitsPoolAddress Units pool address
    /// @param unitTypeId Unit type id
    event UnitsPoolCreated(address unitsPoolAddress, bytes32 unitTypeId);

    /// @notice Emitted when #increaseCorruptionIndex is called
    /// @param settlementAddress An address of settlement which triggered corruptionIndex increase (can be address(0))
    /// @param addedCorruptionIndexAmount Amount of added corruptionIndex
    event CorruptionIndexIncreased(address settlementAddress, uint256 addedCorruptionIndexAmount);

    /// @notice Emitted when #decreaseCorruptionIndex is called
    /// @param settlementAddress An address of settlement which triggered corruptionIndex decrease (can be address(0))
    /// @param reducedCorruptionIndexAmount Amount of reduced corruptionIndex
    event CorruptionIndexDecreased(address settlementAddress, uint256 reducedCorruptionIndexAmount);

    /// @notice Emitted when #updateState is called
    /// @param lastUpdateTime Time at which region time changed
    /// @param lastUpdateRegionTime Region time at 'lastUpdateTime'
    event RegionTimeChanged(uint256 lastUpdateTime, uint256 lastUpdateRegionTime);

    /// @notice Emitted when #handleCultistsSummoned or #handleCultistsDefeated is called
    /// @param newRegionCultistsAmount New region cultists amount
    event RegionCultistsChanged(uint256 newRegionCultistsAmount);

    /// @notice Emitted when #_summonCultists is called
    /// @param newCultistsSummonIntervalNumber New cultists summon interval number
    event LastCultistsSummonIntervalNumberUpdated(uint256 newCultistsSummonIntervalNumber);

    // Errors

    /// @notice Thrown when attempting to call action which can only be called by current era Units
    error OnlyEraUnits();

    // Functions

    /// @notice Persists region time upto specified global time
    /// @dev Even though function is opened, it can only be called by world asset
    /// @param globalTime Global time
    function updateRegionTime(uint256 globalTime) external;

    /// @notice Creates cultists settlement
    /// @dev Even though function is opened, it can only be called by world asset
    /// @param cultistsPosition Cultists position
    function createCultistsSettlement(uint64 cultistsPosition) external;

    /// @notice Buys specified units for specified amount of tokens in current region
    /// @dev If tokensOwner == address(0) -> tokens will be taken from msg.sender
    /// @dev If tokensOwner != address(0) and tokensOwner has given allowance to msg.sender >= amount of tokens for units -> tokens will be taken from tokensOwner
    /// @param tokensOwner Tokens owner
    /// @param settlementAddress Settlement's address army of which will receive units
    /// @param unitTypeIds Unit type ids
    /// @param unitsAmounts Units amounts
    /// @param maxTokensToSell Maximum amounts of tokens to sell for each unit types
    function buyUnitsBatch(
        address tokensOwner,
        address settlementAddress,
        bytes32[] memory unitTypeIds,
        uint256[] memory unitsAmounts,
        uint256[] memory maxTokensToSell
    ) external;

    /// @notice Increases corruptionIndex in region
    /// @dev Even though function is opened, it can only be called by world asset
    /// @param settlementAddress An address of the settlement which triggered corruptionIndex increase (address(0) if triggered by non-settlement action)
    /// @param value Amount of corruptionIndex
    function increaseCorruptionIndex(
        address settlementAddress,
        uint256 value
    ) external;

    /// @notice Decreases corruptionIndex in region
    /// @dev Even though function is opened, it can only be called by world asset
    /// @param settlementAddress An address of the settlement which triggered corruptionIndex decrease (address(0) if triggered by non-settlement action)
    /// @param value Amount of corruptionIndex
    function decreaseCorruptionIndex(
        address settlementAddress,
        uint256 value
    ) external;

    /// @notice Region cultists summon handler
    /// @dev Even though function is opened, it can only be called by world asset
    /// @param value Amount of cultists minted
    function handleCultistsSummoned(
        uint256 value
    ) external;

    /// @notice Region cultists defeat handler
    /// @dev Even though function is opened, it can only be called by world asset
    /// @param value Amount of cultists burned
    function handleCultistsDefeated(
        uint256 value
    ) external;

    /// @notice Calculates penalty according to current cultists count
    /// @dev Uses unit.balanceOf to determine penalty
    /// @return penalty Penalty from cultists
    function getPenaltyFromCultists() external view returns (uint256 penalty);

    /// @notice Updates region state
    /// @dev This function is called every time when production should be modified
    function updateState() external;

    /// @notice Calculates region time with provided timestamp
    /// @dev Takes into an account previous value and current cultists penalty and extrapolates to value at provided timestamp
    /// @param timestamp Timestamp
    /// @return regionTime Extrapolated region time
    function getRegionTime(uint256 timestamp) external view returns (uint256 regionTime);
}
","keccak256":"0xa985fa175f109634d7d040e5f01bce84131215c12c924034bb90d2dcd8f4bbf9","license":"MIT"},"contracts/core/assets/settlement/ISettlement.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.13;

import "../building/IBuilding.sol";
import "../army/IArmy.sol";
import "../siege/ISiege.sol";
import "../region/IRegion.sol";

/// @title Settlement interface
/// @notice Functions to read state/modify state in order to get current settlement parameters and/or interact with it
interface ISettlement {

    // State variables

    /// @notice Region to which this settlement belongs
    /// @dev Immutable, initialized on the settlement creation
    function relatedRegion() external view returns (IRegion);

    /// @notice Banner token id to which current settlement belongs
    /// @dev Immutable, initialized on the settlement creation
    function bannerId() external view returns (uint256);

    /// @notice Siege of the settlement
    /// @dev If any army is besieging settlement not address(0), otherwise address(0)
    function siege() external view returns (ISiege);

    /// @notice Mapping containing settlements buildings
    /// @dev Types of buildings supported can be queried from registry
    function buildings(bytes32 buildingTypeId) external view returns (IBuilding);

    /// @notice Current governors generation
    /// @dev Modified when #removeGovernors is called
    function currentGovernorsGeneration() external view returns (uint256);

    /// @notice Current settlements governors
    /// @dev Modified when #addGovernor or #removeGovernor is called
    function governors(uint256 era, address isGovernor) external view returns (bool);

    /// @notice Settlements army
    /// Immutable, initialized on the settlement creation
    function army() external view returns (IArmy);

    /// @notice Amount of extended prosperity (currently gained units liquidation)
    /// @dev Used for determination amount of real prosperity this settlement has
    function extendedProsperityAmount() external view returns (uint256);

    /// @notice Position on which settlement is created
    /// @dev Immutable, initialized on the settlement creation
    function position() external view returns (uint64);

    /// @notice Amount of corruptionIndex produced by this settlement
    /// @dev Modified when #increaseProducedCorruptionIndex or #decreaseProducedCorruptionIndex is called
    function producedCorruptionIndex() external view returns (int256);

    // Events

    /// @notice Emitted when new building is placed, all building are placed on settlement creation
    /// @param buildingAddress New building address
    /// @param buildingTypeId Building type id
    event BuildingCreated(address buildingAddress, bytes32 buildingTypeId);

    /// @notice Emitted when settlements army is created, is it created on settlement creation
    /// @param armyAddress Army address
    /// @param position Position
    event ArmyCreated(address armyAddress, uint64 position);

    /// @notice Emitted when siege is created on settlement
    /// @param siegeAddress Siege address
    event SiegeCreated(address siegeAddress);

    /// @notice Emitted when #addGovernor or #removeGovernor is called
    /// @param currentGovernorsGeneration Current governors generation
    /// @param governorAddress Address of the governor event is applicable
    /// @param modifiedByAddress Address which modified governor status
    /// @param newStatus Is governor became active/inactive
    event GovernorStatusChanged(uint256 currentGovernorsGeneration, address governorAddress, address modifiedByAddress, bool newStatus);

    /// @notice Emitted when #removeGovernors is called
    /// @param newGovernorsGeneration New governors generation
    event GovernorsGenerationChanged(uint256 newGovernorsGeneration);

    /// @notice Emitted when #destroyRottenSettlement is called
    event Destroyed();

    // Errors

    /// @notice Thrown when attempting to call action which can be called only by settlement owner
    error OnlySettlementOwner();

    /// @notice Thrown when attempting to call action which can be called only by ruler or world asset
    error OnlyRulerOrWorldAsset();

    /// @notice Thrown when attempting to add governor by address which is neither settlement owner or another governor
    error GovernorCannotBeAddedIfSenderNotSettlementOwnerOrAnotherGovernor();

    /// @notice Thrown when attempting to destroy settlement but its not rotten
    error SettlementCannotBeDestroyedIfItsNotRotten();

    /// @notice Thrown when attempting to destroy settlement when it is already rebuilt
    error SettlementCannotBeDestroyedIfItsAlreadyRebuilt();

    /// @notice Thrown when attempting to transfer workers from settlement with non integer value
    error SettlementCannotSendWorkersWithFractions();

    /// @notice Thrown when attempting to transfer workers from settlement to building over maximum allowed workers capacity
    error SettlementCannotSendWorkersToBuildingOverMaximumAllowedCapacity();

    /// @notice Thrown when attempting to decrease corruptionIndex via payment in inactive era
    error SettlementCannotDecreaseCorruptionIndexViaPaymentInInactiveEra();

    /// @notice Thrown when attempting to specify 'tokensAmount' parameter anything but zero whenever world.erc20ForSettlementPurchase is zero address
    error SettlementCannotDecreaseCorruptionIndexViaPaymentWrongParamProvided();

    // Functions

    /// @notice Withdraws resources from settlement to specified address
    /// @dev In case if someone accidentally transfers game resource to the settlement
    /// @param resourceTypeId Resource type id
    /// @param to Address that will receive resources
    /// @param amount Amount to transfer
    function withdrawResources(
        bytes32 resourceTypeId,
        address to,
        uint256 amount
    ) external;

    /// @notice Transfers game resources from msg.sender and workers from settlement to building
    /// @dev Assigns resources+workers to building in single transaction
    /// @dev If resourcesOwner == address(0) -> resources will be taken from msg.sender
    /// @dev If resourcesOwner != address(0) and resourcesOwner has given allowance to msg.sender >= resourcesAmount -> resources will be taken from resourcesOwner
    /// @param buildingAddress Building address
    /// @param workersAmount Workers amount (in 1e18 precision)
    /// @param resourceTypeIds Resource type ids
    /// @param resourcesAmounts Resources amounts
    function assignResourcesAndWorkersToBuilding(
        address resourcesOwner,
        address buildingAddress,
        uint256 workersAmount,
        bytes32[] memory resourceTypeIds,
        uint256[] memory resourcesAmounts
    ) external;

    /// @notice Updates settlement health to current block
    /// @dev Can be called by everyone
    function updateFortHealth() external;

    /// @notice Applies production of every building which produces prosperity
    /// @dev Can be used by everyone
    function updateProsperityAmount() external;

    /// @notice Calculates current settlement owner
    /// @dev Settlements owner is considered an address, which holds bannerId Nft
    /// @return settlementOwner Settlement owner
    function getSettlementOwner() external view returns (address settlementOwner);

    /// @notice Adds settlement governor
    /// @dev Settlement owner and other governor can add governor
    /// @param governorAddress Address to add as the governor
    function addGovernor(address governorAddress) external;

    /// @notice Removes settlement governor
    /// @dev Only settlement owner can remove governor
    /// @param governorAddress Address to remove from governors
    function removeGovernor(address governorAddress) external;

    /// @notice Removes all settlement governors
    /// @dev Only settlement owner can remove all governors
    function removeGovernors() external;

    /// @notice Swaps current settlement prosperity for exact workers
    /// @dev Only ruler or world asset can perform swap
    /// @param workersToBuy Exact amount of workers to buy
    /// @param maxProsperityToSell Maximum amount of prosperity to spend for exact workers
    function swapProsperityForExactWorkers(uint256 workersToBuy, uint256 maxProsperityToSell) external;

    /// @notice Calculates whether provided address is settlement ruler or not
    /// @dev Settlements ruler is an address which owns settlement or an address(es) by which settlement is/are governed
    /// @param potentialRuler Address to check
    /// @return isRuler Banner, whether specified address is ruler or not
    function isRuler(address potentialRuler) external view returns (bool isRuler);

    /// @notice Extends current settlement prosperity by specified amount
    /// @dev Even though function is opened it can be called only by world or world asset
    /// @param prosperityAmount Amount of prosperity to which extend current prosperity
    function extendProsperity(uint256 prosperityAmount) external;

    /// @notice Begins tile capture
    /// @param position Position
    /// @param prosperityStake Prosperity stake
    function beginTileCapture(uint64 position, uint256 prosperityStake) external;

    /// @notice Cancels tile capture
    /// @param position Position
    function cancelTileCapture(uint64 position) external;

    /// @notice Gives up captured tile
    /// @param position Position
    function giveUpCapturedTile(uint64 position) external;

    /// @notice Claims captured tile
    /// @param position Position
    function claimCapturedTile(uint64 position) external;

    /// @notice Increases produced corruptionIndex
    /// @dev Even though function is opened, it can only be called by world asset
    /// @param amount Amount
    function increaseProducedCorruptionIndex(uint256 amount) external;

    /// @notice Decreases produced corruptionIndex
    /// @dev Even though function is opened, it can only be called by world asset
    /// @param amount Amount
    function decreaseProducedCorruptionIndex(uint256 amount) external;

    /// @notice Calculates is settlement rotten or not
    /// @return isRottenSettlement Is rotten settlement
    function isRottenSettlement() external returns (bool isRottenSettlement);

    /// @notice Destroys current settlement
    /// @dev Settlement will be removed only from crossErasMemory in order to give free space to new settlements
    function destroyRottenSettlement() external;

    /// @notice Lowers settlement corruptionIndex by paying to the reward pool
    /// @dev If world.erc20ForSettlementPurchase is address zero -> function is expected to receive Ether as msg.value in order to decrease corruptionIndex. If not address zero -> 'tokensAmount' parameter is used and it will be taken via 'erc20.transferFrom'
    /// @dev Only settlement in active era can decrease its corruptionIndex
    /// @param tokensAmount Amount of tokens will be taken from sender (if world.erc20ForSettlementPurchase is not address zero)
    function payToDecreaseCorruptionIndex(uint256 tokensAmount) external payable;
}
","keccak256":"0x3fd2e3fee2daab2ca99b542f801d86bc023c1ed38c6c48079a080806bc136de8","license":"MIT"},"contracts/core/assets/settlementsMarket/ISettlementMarket.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.13;

import "../region/IRegion.sol";

/// @title Region settlements market interface
/// @notice Functions to read state/modify state in order to buy settlement
interface ISettlementsMarket {

    // State variables

    /// @notice Region to which this market belongs
    /// @dev Immutable, initialized on the market creation
    function relatedRegion() external view returns (IRegion);

    /// @notice Time at which market was created
    /// @dev Immutable, initialized on the market creation
    function marketCreationTime() external view returns (uint256);

    // Events

    /// @notice Emitted when #buySettlement is called
    /// @param settlementAddress Settlement address
    /// @param settlementCost Settlement cost
    event SettlementBought(address settlementAddress, uint256 settlementCost);

    // Errors

    /// @notice Thrown when attempting to buy settlement for free by mighty creator after game began
    error SettlementCannotBeBoughtForFreeAfterGameBegan();

    /// @notice Thrown when attempting to buy settlement for specified banner nft id and not owning it
    error SettlementCannotBeBoughtForNotOwnerBannerNft();

    /// @notice Thrown when attempting to buy settlement on non existent position
    error SettlementCannotBeBoughtOnNonExistentPosition();

    /// @notice Thrown when attempting to buy settlement on position which is not related to this settlement market
    error SettlementCannotBeBoughtOnPositionWhichIsNotRelatedToThisSettlementMarket();

    /// @notice Thrown when attempting to buy settlement due to new settlement cost is higher than max tokens to use specified
    error SettlementCannotBeBoughtDueToCostIsHigherThanMaxTokensToUseSpecified();

    /// @notice Thrown when attempting to buy settlement due to insufficient value sent (only if world.erc20ForSettlementPurchase == address(0), which is equivalent of native token)
    error SettlementCannotBeBoughtDueInsufficientValueSent();

    // Functions

    /// @notice Updates settlement market state to the current block
    /// @dev Called on every action which are based on settlement market state
    function updateState() external;

    /// @notice Buys settlement in region
    /// @dev Even though function is opened, it can only be called by mighty creator and only before game begin time
    /// @param position Position
    /// @param bannerId MithraeumBanners token id which will represent to which settlement will be attached to
    function buySettlementForFreeByMightyCreator(
        uint64 position,
        uint256 bannerId
    ) external;

    /// @notice Buys settlement in region
    /// @dev Tokens will be deducted from msg.sender
    /// @param position Position
    /// @param bannerId MithraeumBanners token id which will represent to which settlement will be attached to
    /// @param maxTokensToUse Maximum amount of tokens to be withdrawn for settlement
    function buySettlement(
        uint64 position,
        uint256 bannerId,
        uint256 maxTokensToUse
    ) external payable;

    /// @notice Returns amount of tokens new settlement will cost
    /// @dev Calculates cost of placing new settlement in tokens
    /// @param timestamp Time at which calculate new settlement cost. If timestamp=0 -> calculates as block.timestamp
    /// @return cost Amount of tokens new settlement will cost
    function getNewSettlementCost(uint256 timestamp) external view returns (uint256 cost);
}
","keccak256":"0xcd00b7c6d08dd7fe44bfb05ed226b4112d88ab2753a34f42c201ee655971d7e2","license":"MIT"},"contracts/core/assets/siege/ISiege.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.13;

import "../settlement/ISettlement.sol";

/// @title Siege interface
/// @notice Functions to read state/modify state in order to get current siege parameters and/or interact with it
interface ISiege {
    struct ArmyInfo {
        uint256 robberyMultiplier;
        uint256 pointsDebt;
        uint256 points;
    }

    // State variables

    /// @notice Settlement address to which this siege belongs
    /// @dev Immutable, initialized on the siege creation
    function relatedSettlement() external view returns (ISettlement);

    /// @notice Mapping containing army information related to current siege
    /// @dev Updated when #modifyArmySiege, #swapRobberyPointsForResourceFromBuildingTreasury is called
    function armyInfo(address armyAddress) external view returns (
        uint256 robberyMultiplier,
        uint256 pointsDebt,
        uint256 points
    );

    /// @notice Mapping containing amount of stored units in siege for specified army
    /// @dev Updated when #modifyArmySiege is called
    function besiegingArmyUnitsByType(address armyAddress, bytes32 unitTypeId) external view returns (uint256);

    /// @notice Total siege power
    /// @dev Updated when #modifyArmySiege is called
    function totalSiegePower() external view returns (uint256);

    /// @notice Amount of robbery point per one damage
    /// @dev Updated when siege parameters related to armies were changed
    function robberyPointsPerOneDamage() external view returns (uint256);

    // Events

    /// @notice Emitted when #modifyArmySiege is called
    /// @param armyAddress Army address
    /// @param unitTypeIds Unit type ids
    /// @param toAddIndication Indication array whether units where added or withdrawn (added = true, withdrawn = false)
    /// @param unitsAmounts Units amounts
    /// @param newRobberyMultiplier New robbery multiplier
    /// @param newTotalSiegePower New total siege power
    event ArmySiegeModified(
        address armyAddress,
        bytes32[] unitTypeIds,
        bool[] toAddIndication,
        uint256[] unitsAmounts,
        uint256 newRobberyMultiplier,
        uint256 newTotalSiegePower
    );

    /// @notice Emitted when army robbery points updated
    /// @param armyAddress Army address
    /// @param buildingAddress Building address
    /// @param stolenAmount Amount of resources stolen
    /// @param burnedAmount Burned amount of resources
    /// @param pointsSpent Amount of points spent
    /// @param newRobberyPointsAmount New robbery points amount
    event BuildingRobbed(
        address armyAddress,
        address buildingAddress,
        uint256 stolenAmount,
        uint256 burnedAmount,
        uint256 pointsSpent,
        uint256 newRobberyPointsAmount
    );

    /// @notice Emitted when #liquidate is called
    /// @param armyAddress Army address which was liquidated
    event ArmyLiquidated(address armyAddress);

    // Errors

    /// @notice Thrown when attempting to liquidate army from siege when it is not liquidatable
    error SiegeCannotLiquidateArmy();

    /// @notice Thrown when attempting to modify siege units with invalid units amount specified
    error SiegeCannotBeModifiedDueToInvalidUnitsAmountSpecified();

    /// @notice Thrown when attempting to swap robbery points with wrong building address specified
    error RobberyPointsSwapNotAllowedDueToSpecifiedBuildingAddressIsNotPartOfEra();

    /// @notice Thrown when attempting to swap robbery points with wrong max points to spend specified
    error RobberyPointsSwapNotAllowedDueToWrongMaxPointsToSpendSpecified();

    /// @notice Thrown when attempting to swap robbery points with specified building address not belonging to the settlement of this siege
    error RobberyPointsSwapNotAllowedDueToSpecifiedBuildingAddressDoesNotBelongToSettlementOfThisSiege();

    /// @notice Thrown when attempting to swap robbery points in result of which zero resources was stolen and burned
    error RobberyPointsSwapNotAllowedDueToNothingWasStolenAndBurned();

    // Functions

    /// @notice Swaps army robbery points for resources from building in related settlement
    /// @dev Even though function is opened, it can be called only by world asset
    /// @param buildingAddress Address of building to rob
    /// @param pointsToSpend Amount of points to spend for robbing
    function swapRobberyPointsForResourceFromBuildingTreasury(address buildingAddress, uint256 pointsToSpend) external;

    /// @notice Updates siege with new amount of damage fort has taken
    /// @dev Even though function is opened, it can be called only by world asset
    /// @param damage Damage which has been done to the settlement
    function applyDamage(uint256 damage) external;

    /// @notice Modifies army robbery multiplier
    /// @dev Even though function is opened, it can be called only by world asset
    /// @param armyAddress Army address
    /// @param unitTypeIds Unit type ids
    /// @param toAddIndication Indication array whether to add units or to withdraw (add = true, withdraw = false)
    /// @param unitsAmounts Units amounts
    /// @param newRobberyMultiplier New robbery multiplier
    function modifyArmySiege(
        address armyAddress,
        bytes32[] calldata unitTypeIds,
        bool[] calldata toAddIndication,
        uint256[] calldata unitsAmounts,
        uint256 newRobberyMultiplier
    ) external;

    /// @notice Calculates if besieging units of provided army can be liquidated from current siege
    /// @dev Does not take into an account if army's battle is ended and army isn't left the battle
    /// @param armyAddress Address of the army
    /// @return canLiquidate Can army be liquidated from current siege
    function canLiquidateArmyBesiegingUnits(address armyAddress) external view returns (bool canLiquidate);

    /// @notice Calculates amount of robbery points army will have at specified time
    /// @dev If timestamp=0, returns value as if timestamp=block.timestamp
    /// @param armyAddress Address of the army
    /// @param timestamp Time at which calculate points
    /// @return robberyPoints Amount of robbery points army will have at specified time
    function getArmyRobberyPoints(address armyAddress, uint256 timestamp) external view returns (uint256 robberyPoints);

    /// @notice Returns amount of besieging units for specified army in siege
    /// @dev Function returns only amounts without types, index in returned array for each unit type is same as in 'registry.getUnits'
    /// @param armyAddress Address of the army
    /// @return unitsAmounts Amount of units that army has in siege
    function getArmyBesiegingUnitsAmounts(address armyAddress) external view returns (uint256[] memory unitsAmounts);

    /// @notice Liquidates army
    /// @dev Can be called by anyone, caller will receive a reward
    /// @param armyAddress Address of army to liquidate
    function liquidate(address armyAddress) external;

    /// @notice Calculates army units siege power
    /// @dev Value are calculated for specified army that is present in siege
    /// @param armyAddress Address of army
    /// @return armySiegePower Army units siege power
    function calculateArmyUnitsSiegePower(address armyAddress) external returns (uint256 armySiegePower);

    /// @notice Calculates army total siege power (including its current robbery multiplier)
    /// @dev Value are calculated for specified army that is present in siege
    /// @param armyAddress Army address
    /// @return armyTotalSiegePower Army total siege power
    function calculateArmyTotalSiegePower(address armyAddress) external returns (uint256 armyTotalSiegePower);
}
","keccak256":"0x8b6630ad0807fa59f241f1454f694d4cfe0ea82130ab9cab0a0d0cb469b304e5","license":"MIT"},"contracts/core/assets/tileCapturingSystem/ITileCapturingSystem.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.13;

/// @title Tile capturing system interface
/// @notice Functions to read state/modify state in order to get current system parameters and/or interact with it
interface ITileCapturingSystem {
    struct TileInfo {
        address ownerSettlementAddress;
        address usurperSettlementAddress;
        uint256 usurperProsperityStake;
        uint64 usurperCaptureBeginTime;
        uint64 usurperCaptureEndTime;
    }

    // State variables

    /// @notice Mapping containing settlements' current capturing tile
    /// @dev Updated when #beginTileCaptureBySettlement or #claimTileCaptureBySettlement, #cancelTileCaptureBySettlement is called
    function settlementCapturingTile(address settlementAddress) external view returns (uint64);

    /// @notice Mapping containing tile info by provided position
    /// @dev Updated when #beginTileCaptureBySettlement or #claimTileCaptureBySettlement, #cancelTileCaptureBySettlement is called
    function tilesInfo(uint64 position) external view returns (
        address ownerSettlementAddress,
        address usurperSettlementAddress,
        uint256 usurperProsperityStake,
        uint64 usurperCaptureBeginTime,
        uint64 usurperCaptureEndTime
    );

    // Events

    /// @notice Emitted when #beginTileCaptureBySettlement is called
    /// @param previousUsurperAddress Previous usurper address
    /// @param position Position
    /// @param settlementAddress Settlement address
    /// @param prosperityStake Prosperity stake
    /// @param captureBeginTime Capture begin time
    /// @param captureEndTime Capture end time
    event TileCapturingBegan(
        address previousUsurperAddress,
        uint64 position,
        address settlementAddress,
        uint256 prosperityStake,
        uint64 captureBeginTime,
        uint64 captureEndTime
    );

    /// @notice Emitted when #cancelTileCaptureBySettlement
    /// @param position Position
    /// @param settlementAddress Settlement address
    event TileCapturingCancelled(
        uint64 position,
        address settlementAddress
    );

    /// @notice Emitted when #claimTileCaptureBySettlement
    /// @param previousSettlementOwnerAddress Previous settlement owner address
    /// @param position Position
    /// @param settlementAddress Settlement address
    /// @param prosperityStake Prosperity stake
    event CapturedTileClaimed(
        address previousSettlementOwnerAddress,
        uint64 position,
        address settlementAddress,
        uint256 prosperityStake
    );

    /// @notice Emitted when #giveUpCapturedTile
    /// @param position Position
    /// @param settlementAddress Settlement address
    event CapturedTileGivenUp(
        uint64 position,
        address settlementAddress
    );

    // Errors

    /// @notice Thrown when attempting to begin tile capture of non existent position
    error CannotBeginTileCaptureDueToNonExistentPositionSpecified();

    /// @notice Thrown when attempting to begin tile capture of position on not activated region
    error CannotBeginTileCaptureOnNotActivatedRegion();

    /// @notice Thrown when attempting to begin tile capture on position with settlement on it
    error CannotBeginTileCaptureOnPositionWithSettlement();

    /// @notice Thrown when attempting to begin tile capture by settlement which is already capturing tile
    error CannotBeginTileCaptureBySettlementWhichIsAlreadyCapturingTile();

    /// @notice Thrown when attempting to begin tile capture by settlement which already has maximum allowed tiles with same bonus as specified tile
    error CannotBeginTileCaptureBySettlementAlreadyHavingMaximumCapturedTilesWithSameBonus();

    /// @notice Thrown when attempting to begin tile capture of position without tile bonus
    error CannotBeginTileCaptureOfPositionWithoutBonus();

    /// @notice Thrown when attempting to begin tile capture with not having specified amount of prosperity
    error CannotBeginTileCaptureDueToNotHavingSpecifiedProsperity();

    /// @notice Thrown when attempting to begin tile capture with prosperity stake lower than next minimum prosperity stake
    error CannotBeginTileCaptureDueToNotReachedNextMinimumProsperityStake();

    /// @notice Thrown when attempting to cancel tile capture by settlement which is not currently capturing specified tile
    error TileCaptureCannotBeCancelledBySettlementWhichIsNotCurrentTileUsurper();

    /// @notice Thrown when attempting to give up captured tile by settlement which is not owner of specified tile
    error CapturedTileCannotBeGivenUpByNonSettlementOwner();

    /// @notice Thrown when attempting to claim captured tile by settlement which was not capturing specified tile
    error ClaimTileCaptureCannotBeDoneByNonUsurperSettlement();

    /// @notice Thrown when attempting to claim captured tile at this time (it is still capturing)
    error ClaimTileCaptureCannotBeDoneAtThisTime();

    /// @notice Thrown when attempting to claim captured tile without necessary prosperity in settlement
    error ClaimTileCaptureCannotBeDoneWithoutNecessaryProsperity();

    /// @notice Thrown when attempting to apply or remove tile bonus. It should not be thrown ever, if it does this will indicate logic error
    error UnknownTileBonus();

    // Functions

    /// @notice Begins tile capturing
    /// @dev Even though function is opened, it can be called only by world asset
    /// @param settlementAddress Settlement address
    /// @param position Position
    function beginTileCapture(address settlementAddress, uint64 position, uint256 prosperityStake) external;

    /// @notice Cancels tile capturing
    /// @dev Even though function is opened, it can be called only by world asset
    /// @param settlementAddress Settlement address
    /// @param position Position
    function cancelTileCapture(address settlementAddress, uint64 position) external;

    /// @notice Gives up captured tile
    /// @dev Even though function is opened, it can be called only by world asset
    /// @param settlementAddress Settlement address
    /// @param position Position
    function giveUpCapturedTile(address settlementAddress, uint64 position) external;

    /// @notice Claims captured tile
    /// @dev Even though function is opened, it can be called only by world asset
    /// @param settlementAddress Settlement address
    /// @param position Position
    function claimTileCapture(address settlementAddress, uint64 position) external;

    /// @notice Returns positions of captured tiles for provided settlement address
    /// @dev Returns only claimed tiles
    /// @param settlementAddress Settlement address
    /// @param tileBonusType Tile bonus type
    /// @return positions Positions of captured tiles
    function getCapturedTilesBySettlementAddress(address settlementAddress, uint8 tileBonusType) external view returns (uint64[] memory positions);

    /// @notice New settlement handler
    /// @dev Even though function is opened, it can be called only by world asset
    /// @param position Position
    function handleSettlementCreatedOnPosition(uint64 position) external;
}
","keccak256":"0x380db93cc220f6a77460247375dd108e6fbc29705fa5bd6f0173597d67b8baa9","license":"MIT"},"contracts/core/assets/tokens/prosperity/IProsperity.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.13;

import "../../../../token/IERC20Burnable.sol";
import "../../../../token/IERC20Int.sol";

/// @title Resource interface
/// @notice Functions to read state/modify state in order to get current prosperity parameters and/or interact with it
interface IProsperity is IERC20Int, IERC20Burnable {
    // State variables

    /// @notice Mapping containing amount of prosperity spend for workers buying
    /// @dev Only settlements can spend prosperity for workers
    /// @param settlementAddress Address of settlement
    /// @return amount Amount of prosperity spend for workers buying
    function prosperitySpent(address settlementAddress) external view returns (uint256 amount);

    // Errors

    /// @notice Thrown when attempting to call action which is disabled
    error Disabled();

    // Functions

    /// @notice Spends prosperity for specified settlement address
    /// @dev Called for settlement when settlement is buying workers
    /// @param settlementAddress Address of settlement
    /// @param amount Amount of prosperity spend for workers buying
    function spend(address settlementAddress, uint256 amount) external;

    /// @notice Mints prosperity to specified address
    /// @dev Even though function is opened, it can only be called by world asset
    /// @param to Address which will receive prosperity
    /// @param amount Amount of prosperity to mint
    function mint(address to, uint256 amount) external;
}
","keccak256":"0xe5b33625559b153f089e20a0a2c52d9cfc0ea38ecfea6e492a3047a7e6e0346c","license":"MIT"},"contracts/core/assets/tokens/resources/IResource.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.13;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "../../../../token/IERC20Burnable.sol";

/// @title Resource interface
/// @notice Functions to read state/modify state in order to get current resource parameters and/or interact with it
interface IResource is IERC20Burnable, IERC20 {
    // State variables

    /// @notice Token name
    /// @dev Immutable, initialized on creation
    function tokenName() external returns (string memory);

    /// @notice Token symbol
    /// @dev Immutable, initialized on creation
    function tokenSymbol() external returns (string memory);

    /// @notice Resource type id
    /// @dev Immutable, initialized on creation
    function resourceTypeId() external returns (bytes32);

    // Functions

    /// @notice Thrown when attempting to call action which only possible to be called by world asset or reward pool
    error OnlyWorldAssetFromSameEraOrRewardPool();

    /// @notice Thrown when attempting to transfer resources to building which does not use this resource
    error ResourceNotAcceptable();

    /// @notice Returns state balance for specified token owner
    /// @dev Current function returns value of balances 'as is', without recalculation (same as 'balanceOf' you would expect)
    /// @param tokensOwner Tokens owner
    /// @return balance Balance for token owner
    function stateBalanceOf(address tokensOwner) external view returns (uint256 balance);

    /// @notice Mints resource to specified address
    /// @dev Even though function is opened, it can only be called by world asset
    /// @param to Address which will receive resources
    /// @param amount Amount of resources to mint
    function mint(address to, uint256 amount) external;

    /// @notice Spends allowance (same as ERC20._spendAllowance)
    /// @dev Even though function is opened, it can be called only by world asset
    function spendAllowance(
        address owner,
        address spender,
        uint256 amount
    ) external;
}
